cy.intercept({
  method: "GET",
  url: "/v2/cocktail/*"
}).as("cocktail");
cy.request({
  method: 'GET',
  url: 'https://whale-app-iz3av.ondigitalocean.app/v2/filter?page=0',
  headers: {
    'Content-Type': 'application/json'
  }
}).then(cocktailsList => {
  const cocktails = cocktailsList.body.cocktails;
  cy.get(_homePage.default.sorting.byRate).should("be.visible");
  cy.get(_homePage.default.search).should("be.visible");
  cy.get(_homePage.default.cocktailCard).each(item => {
    const itemText = item.text().trim();
    expect(itemText).to.contain(cocktails[item.index()].name);
  });
  cy.get(_homePage.default.cocktailCard).then(cocktails => {
    const cocktailsArray = cocktails.toArray();
    cy.wrap(Cypress._.sample(cocktailsArray)).click();
  });
  cy.wait('@cocktail').then(({
    response: {
      body: cocktail
    }
  }) => {
    cy.get(_cocktailPage.default.title).should('contain', cocktail.name);
    cy.get(_cocktailPage.default.recipeItem).each(listItem => {
      const itemText = listItem.text();
      expect(itemText).to.equal(cocktail.receipt[listItem.index()]);
    });
    cy.get(_cocktailPage.default.component).each(listItem => {
      const itemText = listItem.text();
      expect(itemText).contains(cocktail.goods[listItem.index()].name);
    });
    cy.get(_cocktailPage.default.tools).each(listItem => {
      const itemText = listItem.text();
      expect(itemText).contains(cocktail.tools[listItem.index()].name);
    });
  });
});

