interceptFiltering("alcohol-volume=slaboalkoholni");
let firstItemName;
cy.get(_homePage.default.cocktailCard).find(".cart__name").first().invoke('text').then(itemTitle => {
  firstItemName = itemTitle;
});
// applying the low alcohol filter
cy.get(_homePage.default.filters.lowalcohol).click();
// checking that proper tag is shown
cy.get(_homePage.default.filters.lowalcoholTag).should('be.visible');
cy.wait("@sortingApplied").then(({
  response: {
    body: {
      cocktails: cocktails
    }
  }
}) => {
  cy.get(_homePage.default.cocktailCard).each(item => {
    const itemText = item.text().trim();
    expect(itemText).to.contain(cocktails[item.index()].name);
  });
  // checking that the first element name is changed after applying filters
  cy.get(_homePage.default.cocktailCard).find(".cart__name").first().should('not.contain', firstItemName);
});

