cy.intercept({
  method: "GET",
  url: "/v2/cocktails/full*"
}).as("cocktail");
cy.request({
  method: 'GET',
  url: 'https://whale-app-iz3av.ondigitalocean.app/v2/search/cocktails?page=0',
  headers: {
    'Content-Type': 'application/json'
  }
}).then(cocktailsList => {
  const cocktails = cocktailsList.body.cocktails;
  cy.get(_homePage.default.vertical.selector.sorting.byRate).should("be.visible");
  cy.get(_homePage.default.vertical.selector.search).should("be.visible");
  cy.get(_homePage.default.vertical.selector.cocktailCard).each(item => {
    const itemText = item.text().trim();
    expect(itemText).to.contain(cocktails[item.index()].name);
  });
  cy.get(_homePage.default.vertical.selector.cocktailCard).eq(5).click();
  cy.wait('@cocktail').then(cocktail => {
    const details = cocktail.response.body;
    const receipt = details.receipt;
    const goods = details.goods;
    const tools = details.tools;
    cy.get(_cocktailPage.default.vertical.selector.title).should('contain', details.name);
    cy.get(_cocktailPage.default.vertical.selector.recipeItem).each(listItem => {
      const itemText = listItem.text();
      expect(itemText).to.equal(receipt[listItem.index()]);
    });
    cy.get(_cocktailPage.default.vertical.selector.component).find('[data-cy="ingredient"]').each(listItem => {
      const itemText = listItem.text();
      expect(itemText).contains(goods[listItem.index()].name);
    });
    cy.get(_cocktailPage.default.vertical.selector.tools).find('[data-cy="ingredient"]').each(listItem => {
      const itemText = listItem.text();
      expect(itemText).contains(tools[listItem.index()].name);
    });
  });
});

