interceptSorting("alcohol-volume=1");
let firstItemName;
cy.get(_homePage.default.vertical.selector.cocktailCard).find(".cart__name").first().invoke('text').then(itemTitle => {
  firstItemName = itemTitle;
});
// applying the low alcohol filter
cy.get(_homePage.default.vertical.selector.filters.lowalcohol).click();
// checking that proper tag is shown
cy.get(_homePage.default.vertical.selector.filters.lowalcoholTag).should('be.visible');
// applying the low alcohol filter again because when it clicked programatically the request is not sent
cy.get(_homePage.default.vertical.selector.filters.lowalcohol).click();
cy.wait("@sortingApplied").its('response.body').then(body => {
  const cocktails = body.cocktails;
  cy.get(_homePage.default.vertical.selector.cocktailCard).each(item => {
    const itemText = item.text().trim();
    expect(itemText).to.contain(cocktails[item.index()].name);
  });
  // checking that the first element name is changed after applying filters
  cy.get(_homePage.default.vertical.selector.cocktailCard).find(".cart__name").first().should('not.contain', firstItemName);
});

